import React, { useState } from "react";
import "../css/Projects.css";

const projectsData = [
  { id: 1, title: "프로젝트 1", description: "간단한 설명 1", image: project1, details: "상세 내용 1" },
  { id: 2, title: "프로젝트 2", description: "간단한 설명 2", image: project2, details: "상세 내용 2" },
  // 추가 프로젝트...
];

function Projects() {
  const [selected, setSelected] = useState(null);

  const openModal = (project) => setSelected(project);
  const closeModal = () => setSelected(null);

  return (
    <section id="projects">
      <h2>Projects</h2>
      <div className="project-cards">
        {projectsData.map((p) => (
          <div key={p.id} className="project-card" onClick={() => openModal(p)}>
            <img src={p.image} alt={p.title} className="project-img" />
            <h3>{p.title}</h3>
            <p>{p.description}</p>
          </div>
        ))}
      </div>

      {selected && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-btn" onClick={closeModal}>&times;</span>
            <h3>{selected.title}</h3>
            <img src={selected.image} alt={selected.title} className="modal-img" />
            <p>{selected.details}</p>
          </div>
        </div>
      )}
    </section>
  );
}

export default Projects;
