import React from "react";
import profileImg from "../assets/images/front_bg.jpg";
import "../css/Intro.css";

function Intro() {
  return (
    <section id="intro">
      <img id="profileImg" src={profileImg} alt="프로필 이미지" width="150" />
      <h1 id="mainTitle">이재용 포트폴리오</h1>
      <p id="mainDesc">프론트엔드 개발자</p>

      {/* 스크롤 안내 아이콘 */}
      <div id="scrollIcon">
        <div className="mouse">
          <div className="wheel"></div>
        </div>
        <span>Scroll Down</span>
      </div>
    </section>
  );
}

export default Intro;
