import React, { useState } from "react";
import "../css/Projects.css";

// 프로젝트 예제 데이터 (6개)
const projectsData = [
  {
    id: 1,
    title: "프로젝트 1",
    description: "간단한 설명 1",
    image: "/assets/images/project1.jpg",
    details: "프로젝트 1 상세 내용입니다. 사용 기술, 기능, 구현 방법 등을 작성하세요."
  },
  {
    id: 2,
    title: "프로젝트 2",
    description: "간단한 설명 2",
    image: "/assets/images/project2.jpg",
    details: "프로젝트 2 상세 내용입니다. 사용 기술, 기능, 구현 방법 등을 작성하세요."
  },
  {
    id: 3,
    title: "프로젝트 3",
    description: "간단한 설명 3",
    image: "/assets/images/project3.jpg",
    details: "프로젝트 3 상세 내용입니다. 사용 기술, 기능, 구현 방법 등을 작성하세요."
  },
  {
    id: 4,
    title: "프로젝트 4",
    description: "간단한 설명 4",
    image: "/assets/images/project4.jpg",
    details: "프로젝트 4 상세 내용입니다. 사용 기술, 기능, 구현 방법 등을 작성하세요."
  },
  {
    id: 5,
    title: "프로젝트 5",
    description: "간단한 설명 5",
    image: "/assets/images/project5.jpg",
    details: "프로젝트 5 상세 내용입니다. 사용 기술, 기능, 구현 방법 등을 작성하세요."
  },
  {
    id: 6,
    title: "프로젝트 6",
    description: "간단한 설명 6",
    image: "/assets/images/project6.jpg",
    details: "프로젝트 6 상세 내용입니다. 사용 기술, 기능, 구현 방법 등을 작성하세요."
  }
];

function Projects() {
  const [selectedProject, setSelectedProject] = useState(null);

  const openModal = (project) => setSelectedProject(project);
  const closeModal = () => setSelectedProject(null);

  return (
    <section id="projects">
      <h2>Projects</h2>
      <div className="project-cards">
        {projectsData.map((project) => (
          <div key={project.id} className="project-card" onClick={() => openModal(project)}>
            <img src={project.image} alt={project.title} className="project-img" />
            <h3>{project.title}</h3>
            <p>{project.description}</p>
          </div>
        ))}
      </div>

      {selectedProject && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-btn" onClick={closeModal}>&times;</span>
            <h3>{selectedProject.title}</h3>
            <img src={selectedProject.image} alt={selectedProject.title} className="modal-img" />
            <p>{selectedProject.details}</p>
          </div>
        </div>
      )}
    </section>
  );
}

export default Projects;
