import React from "react";
import "../css/Skill.css";

// 기술 데이터
const skills = [
  {
    category: "Back-end",
    items: [
      { name: "Java", img: "/assets/incons/icon_java.png" },
      { name: "Spring", img: "/assets/incons/icon_spring.png" },
      { name: "Spring Boot", img: "/assets/incons/icon_springboot.png" },
      { name: "MyBatis", img: "/assets/incons/icon_mybatis.png" },
      { name: "REST API", img: "/assets/incons/icon_restapi.png" },
    ],
  },
  {
    category: "Front-end",
    items: [
      { name: "HTML", img: "/assets/incons/icon_html.png" },
      { name: "CSS", img: "/assets/incons/icon_css.png" },
      { name: "JavaScript", img: "/assets/incons/icon_js.png" },
      { name: "React", img: "/assets/incons/icon_react.png" },
      { name: "jQuery", img: "/assets/incons/icon_jquery.png" },
    ],
  },
  {
    category: "Tools",
    items: [
      { name: "Eclipse", img: "/assets/incons/icon_eclipse.png" },
      { name: "IntelliJ", img: "/assets/incons/icon_intellij.png" },
      { name: "VSCode", img: "/assets/incons/icon_vscode.png" },
      { name: "Oracle SQL Developer", img: "/assets/incons/icon_oracle.png" },
      { name: "ERD Tool", img: "/assets/incons/icon_erd.png" },
      { name: "Postman", img: "/assets/incons/icon_postman.png" },
    ],
  }
];
const skillsflex = [
  {
    category: "Server",
    items: [
      { name: "Tomcat", img: "/assets/incons/icon_tomcat.png" },
      { name: "EC2", img: "/assets/incons/icon_ec2.png" },
    ],
  },
  {
    category: "DB",
    items: [{ name: "Oracle", img: "/assets/incons/icon_oracle.png" }],
  }
];

function Skill() {
  return (
    <section id="skills">
      <h2>Skills</h2>

      {skills.map((skillCategory, idx) => (
        <div key={idx} className="skill-category">
          <h3>{skillCategory.category}</h3>
          <div className="skill-cards">
            {skillCategory.items.map((skill, i) => (
              <div key={i} className="skill-card">
                {skill.img && (
                  <div className="skill-img">
                    <img src={skill.img} alt={skill.name} />
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
      <div className="skillsflex">
          {skillsflex.map((skillCategory, idx) => (
            <div key={idx} className="skill-category">
              <h3>{skillCategory.category}</h3>
              <div className="skill-cards">
                {skillCategory.items.map((skill, i) => (
                  <div key={i} className="skill-card">
                    {skill.img && (
                      <div className="skill-img">
                        <img src={skill.img} alt={skill.name} />
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          ))}
      </div>
    </section>
  );
}

export default Skill;
