import React, { useState, useEffect } from "react";
import "../css/Projects.css";

import project1 from "../assets/images/project1_portfolio.png";
import project2 from "../assets/images/project1_portfolio.png";
import project3 from "../assets/images/project1_portfolio.png";
import project4 from "../assets/images/project1_portfolio.png";
import project5 from "../assets/images/project1_portfolio.png";
import project6 from "../assets/images/project1_portfolio.png";

const projectsData = [
  { id: 1, title: "프로젝트 1", description: "계좌관리, 금융 상품 및 서비스를 조회하고 신청이 가능한, 편리한 금융 서비스 제공 및 고객 경험 개선을한 금융 서비스 플랫폼입니다.", image: project1, details: "계좌관리, 금융 상품 및 서비스를 조회하고 신청이 가능한, 편리한 금융 서비스 제공 및 고객 경험 개선을한 금융 서비스 플랫폼입니다." },
  { id: 2, title: "프로젝트 2", description: "병원 약국 통합 검색, 예약 및 의학 정보 시스템은 다양한 의학 정보 제공하는 플랫폼입니다.", image: project2, details: "병원 약국 통합 검색, 예약 및 의학 정보 시스템은 다양한 의료 기관 및 약국 정보 검색, 예약 기능, 의학 정보 제공을 통해 사용자들이 효율적으로 의료 서비스를 이용할 수 있도록 구축된 플랫폼이다." },
  { id: 3, title: "프로젝트 3", description: "간단한 설명 3", image: project3, details: "상세 내용 3" },
  { id: 4, title: "프로젝트 4", description: "간단한 설명 4", image: project4, details: "상세 내용 4" },
  { id: 5, title: "프로젝트 5", description: "간단한 설명 5", image: project5, details: "상세 내용 5" },
  { id: 6, title: "프로젝트 6", description: "간단한 설명 6", image: project6, details: "상세 내용 6" },
];

function Projects() {
  const [selected, setSelected] = useState(null);

  const openModal = (project) => setSelected(project);
  const closeModal = () => setSelected(null);

  // ESC 키로 모달 닫기
  useEffect(() => {
    const handleEsc = (e) => {
      if (e.key === "Escape") closeModal();
    };
    window.addEventListener("keydown", handleEsc);
    return () => window.removeEventListener("keydown", handleEsc);
  }, []);

  return (
    <section id="projects">
      <h2>Projects</h2>
      <div className="project-cards">
        {projectsData.map((p) => (
          <div key={p.id} className="project-card" onClick={() => openModal(p)}>
            <img src={p.image} alt={p.title} className="project-img" />
            <h3>{p.title}</h3>
            <p>{p.description}</p>
          </div>
        ))}
      </div>

      {selected && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-btn" onClick={closeModal}>&times;</span>
            <h3>{selected.title}</h3>
            <img src={selected.image} alt={selected.title} className="modal-img" />
            <p>{selected.details}</p>
          </div>
        </div>
      )}
    </section>
  );
}

export default Projects;
