import React from "react";
import profileImg from "../assets/images/front_bg.jpg"; // 경로 맞춰서 변경

function Intro() {
  const scrollToAbout = () => {
    document.getElementById("aboutme")?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <section id="intro">
      <img id="profileImg" src={profileImg} alt="프로필 이미지" width="150"/>
      <h1 id="mainTitle">최지은 포트폴리오</h1>
      <p id="mainDesc">프론트엔드 개발자</p>
      <div id="socialLinks">
        <a href="#">GitHub</a> | <a href="#">LinkedIn</a>
      </div>
      <button id="scrollBtn" onClick={scrollToAbout}>About Me로 이동</button>
    </section>
  );
}

export default Intro;
