import React, { useState, useEffect } from "react";
import "../css/Projects.css";

import project1_1 from "../assets/images/project1_portfolio1.png";
import project1_2 from "../assets/images/project1_portfolio2.png";
/*import project1_3 from "../assets/images/project1_portfolio3.png";
import project2_1 from "../assets/images/project2_portfolio1.png";
import project2_2 from "../assets/images/project2_portfolio1.png";
import project3_1 from "../assets/images/project1_portfolio1.png";
import project3_2 from "../assets/images/project2_portfolio1.png";
import project4_1 from "../assets/images/project1_portfolio1.png";
import project4_2 from "../assets/images/project2_portfolio1.png";*/

const projectsData = [
    {
        id: 1,
        title: "프로젝트 1",
        period: "2023.01 - 2023.06",
        description: "간단한 설명 1",
        details: "상세 내용 1",
        features: ["기능 1", "기능 2", "기능 3"],
        tech: ["Java", "Spring", "React"],
        contribution: "전체 개발 참여",
        images: [project1_1, project1_2],
    },
    {
        id: 2,
        title: "프로젝트 1",
        period: "2023.01 - 2023.06",
        description: "간단한 설명 1",
        details: "상세 내용 1",
        features: ["기능 1", "기능 2", "기능 3"],
        tech: ["Java", "Spring", "React"],
        contribution: "전체 개발 참여",
        images: [project1_1, project1_2],
    },
    {
        id: 3,
        title: "프로젝트 1",
        period: "2023.01 - 2023.06",
        description: "간단한 설명 1",
        details: "상세 내용 1",
        features: ["기능 1", "기능 2", "기능 3"],
        tech: ["Java", "Spring", "React"],
        contribution: "전체 개발 참여",
        images: [project1_1, project1_2],
    },
    {
        id: 4,
        title: "프로젝트 1",
        period: "2023.01 - 2023.06",
        description: "간단한 설명 1",
        details: "상세 내용 1",
        features: ["기능 1", "기능 2", "기능 3"],
        tech: ["Java", "Spring", "React"],
        contribution: "전체 개발 참여",
        images: [project1_1, project1_2],
    },

];

function Projects() {
    const [selected, setSelected] = useState(null);
    const [currentImg, setCurrentImg] = useState(0);

    const openModal = (project) => {
      setSelected(project);
      setCurrentImg(0);
      document.body.style.overflow = "hidden"; // 배경 스크롤 막기
    };

    const closeModal = () => {
      setSelected(null);
      document.body.style.overflow = "auto"; // 배경 스크롤 다시 가능
    };

    const prevImage = () => {
        if (!selected) return;
        setCurrentImg(
            (prev) => (prev - 1 + selected.images.length) % selected.images.length
        );
    };

    const nextImage = () => {
        if (!selected) return;
        setCurrentImg((prev) => (prev + 1) % selected.images.length);
    };

    // ESC 키로 모달 닫기, 스크롤
    useEffect(() => {
      const handleEsc = (e) => {
        if (e.key === "Escape") closeModal();
      };

      const handleWheel = (e) => {
        const modal = document.querySelector(".modal-content");
        if (!modal) return;

        const scrollTop = modal.scrollTop;
        const scrollHeight = modal.scrollHeight;
        const clientHeight = modal.clientHeight;

        const delta = e.deltaY;

        // 모달 스크롤 범위 벗어나면 페이지 스크롤 방지
        if ((delta < 0 && scrollTop === 0) || (delta > 0 && scrollTop + clientHeight >= scrollHeight)) {
          e.preventDefault();
        }
      };

      window.addEventListener("keydown", handleEsc);
      window.addEventListener("wheel", handleWheel, { passive: false });

      return () => {
        window.removeEventListener("keydown", handleEsc);
        window.removeEventListener("wheel", handleWheel);
      };
    }, []);


    return (
        <section id="projects">
          <h2>Projects</h2>
          <div className="project-cards">
            {projectsData.map((p) => (
              <div key={p.id} className="project-card" onClick={() => openModal(p)}>
                <img src={p.images[0]} alt={p.title} className="project-img" />
                <h3>{p.title}</h3>
                <p>{p.description}</p>
              </div>
            ))}
          </div>

          {selected && (
            <div className="modal-overlay" onClick={closeModal}>
              <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <span className="close-btn" onClick={closeModal}>
                  &times;
                </span>

                {/* 프로젝트 제목 & 기간 */}
                <h3>{selected.title}</h3>
                <p className="duration">{selected.period}</p>

                {/* 이미지 슬라이드 */}
                {selected.images.length > 0 && (
                  <div className="image-slider">
                    <button className="prev-btn" onClick={prevImage}>
                      &#10094;
                    </button>
                    <img
                      src={selected.images[currentImg]}
                      alt={`${selected.title} ${currentImg + 1}`}
                    />
                    <button className="next-btn" onClick={nextImage}>
                      &#10095;
                    </button>
                  </div>
                )}

                {/* 간단한 설명 */}
                <p className="description">{selected.description}</p>

                {/* 주요 기능 */}
                {selected.features && (
                  <div className="features">
                    <h4>주요 기능 및 특징</h4>
                    <ul>
                      {selected.features.map((f, i) => (
                        <li key={i}>{f}</li>
                      ))}
                    </ul>
                  </div>
                )}

                {/* 사용 기술 */}
                {selected.tech && (
                  <div className="technologies">
                    <h4>사용 기술 및 언어</h4>
                    <p>{selected.tech.join(", ")}</p>
                  </div>
                )}

                {/* 작업 기여도 */}
                {selected.contribution && (
                  <div className="contribution">
                    <h4>작업 기여도</h4>
                    <p>{selected.contribution}</p>
                  </div>
                )}
              </div>
            </div>
          )}
        </section>
      );
}

export default Projects;
