import React, { useState, useEffect } from "react";
import "../css/Projects.css";

import project1_1 from "../assets/images/project1_portfolio.png";
import project1_2 from "../assets/images/project2_portfolio.png";
import project2_1 from "../assets/images/project1_portfolio.png";
import project2_2 from "../assets/images/project2_portfolio.png";
import project3_1 from "../assets/images/project1_portfolio.png";
import project3_2 from "../assets/images/project2_portfolio.png";

const projectsData = [
  { id: 1, title: "아파! 어디가?",
    description: "증상을 선택하여 병원을 검색 및 예약 하며 의학 정보를 제공하는 플랫폼입니다.",
    images: [project1_1, project1_2],
    details: "병원 약국 통합 검색, 예약 및 의학 정보 시스템은 다양한 의료 기관 및 약국 정보 검색, 예약 기능, 의학 정보 제공을 통해 사용자들이 효율적으로 의료 서비스를 이용할 수 있도록 구축된 플랫폼이다." },
  { id: 2, title: "아틀란 은행",
    description: "계좌관리, 금융 상품 및 서비스를 조회하고 신청이 가능한 금융 서비스 제공하는 금융 서비스 플랫폼입니다.",
    image: project2,
    details: "계좌관리, 금융 상품 및 서비스를 조회하고 신청이 가능한, 편리한 금융 서비스 제공 및 고객 경험 개선을한 금융 서비스 플랫폼입니다." },
  { id: 3, title: "프로젝트 3", description: "간단한 설명 3", image: project3, details: "상세 내용 3" },
  { id: 4, title: "프로젝트 4", description: "간단한 설명 4", image: project4, details: "상세 내용 4" },
];

function Projects() {
  const [selected, setSelected] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);

   useEffect(() => {
      setCurrentIndex(0); // 모달 열 때 항상 첫 이미지부터 시작
    }, [selected]);

  const openModal = (project) => setSelected(project);
  const closeModal = () => setSelected(null);

  const prevImage = (e) => {
      e.stopPropagation();
      setCurrentIndex((prev) => (prev === 0 ? selected.images.length - 1 : prev - 1));
    };

  const nextImage = (e) => {
      e.stopPropagation();
      setCurrentIndex((prev) => (prev === selected.images.length - 1 ? 0 : prev + 1));
  };

  // ESC 키로 모달 닫기
  useEffect(() => {
    const handleEsc = (e) => {
      if (e.key === "Escape") closeModal();
    };
    window.addEventListener("keydown", handleEsc);
    return () => window.removeEventListener("keydown", handleEsc);
  }, []);

  return (
    <section id="projects">
      <h2>Projects</h2>
      <div className="project-cards">
        {projectsData.map((p) => (
          <div key={p.id} className="project-card" onClick={() => openModal(p)}>
            <img src={p.image} alt={p.title} className="project-img" />
            <h3>{p.title}</h3>
            <p>{p.description}</p>
          </div>
        ))}
      </div>

      {selected && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <span className="close-btn" onClick={closeModal}>&times;</span>
            <h3>{selected.title}</h3>
            <div className="modal-slider">
              <button className="prev" onClick={() => prevImage()}>&lt;</button>
              <img src={selected.images[currentIndex]} alt={`${selected.title}-${currentIndex}`} />
              <button className="next" onClick={() => nextImage()}>&gt;</button>
            </div>
            <p>{selected.details}</p>
          </div>
        </div>
      )}
    </section>
  );
}

export default Projects;
