import React from "react";

export default function Modal({ show, onClose, title, description }) {
  if (!show) return null;

  return (
    <div style={{
      position: "fixed",
      top: 0, left: 0,
      width: "100%", height: "100%",
      backgroundColor: "rgba(0,0,0,0.5)",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      zIndex: 1000
    }}>
      <div style={{
        background: "#fff",
        padding: "20px",
        borderRadius: "10px",
        width: "400px",
        maxWidth: "90%",
        textAlign: "center",
        position: "relative"
      }}>
        <h3>{title}</h3>
        <p>{description}</p>
        <button onClick={onClose} style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          cursor: "pointer"
        }}>X</button>
      </div>
    </div>
  );
}
