import React from "react";
import "/css/Skill.css"; // 스타일을 따로 관리

// 기술 데이터
const skills = [
  {
    category: "Back-end",
    items: [
      { name: "Java", img: "/assets/images/java.png" },
      { name: "Spring", img: "/assets/images/spring.png" },
      { name: "Spring Boot", img: "/assets/images/springboot.png" },
      { name: "MyBatis", img: "/assets/images/mybatis.png" },
      { name: "REST API", img: "/assets/images/restapi.png" },
    ],
  },
  {
    category: "Front-end",
    items: [
      { name: "HTML", img: "/assets/images/html.png" },
      { name: "CSS", img: "/assets/images/css.png" },
      { name: "JavaScript", img: "/assets/images/js.png" },
      { name: "React", img: "/assets/images/react.png" },
      // jQuery는 선택적 포함 가능
      { name: "jQuery", img: "/assets/images/jquery.png" },
    ],
  },
  {
    category: "Tools",
    items: [
      { name: "Eclipse", img: "/assets/images/eclipse.png" },
      { name: "IntelliJ", img: "/assets/images/intellij.png" },
      { name: "VSCode", img: "/assets/images/vscode.png" },
      { name: "Oracle SQL Developer", img: "/assets/images/oracle.png" },
      { name: "ERD Tool", img: "/assets/images/erd.png" },
      { name: "Postman", img: "/assets/images/postman.png" },
    ],
  },
  {
    category: "Server",
    items: [
      { name: "Tomcat", img: "/assets/images/tomcat.png" },
      { name: "EC2", img: "/assets/images/ec2.png" },
    ],
  },
  {
    category: "DB",
    items: [{ name: "Oracle", img: "/assets/images/oracle.png" }],
  },
];

function Skill() {
  return (
    <section id="skills">
      <h2>Skills</h2>
      {skills.map((skillCategory, idx) => (
        <div key={idx} className="skill-category">
          <h3>{skillCategory.category}</h3>
          <div className="skill-cards">
            {skillCategory.items.map((skill, i) => (
              <div key={i} className="skill-card">
                {skill.img && (
                  <div className="skill-img">
                    <img src={skill.img} alt={skill.name} />
                  </div>
                )}
                <span>{skill.name}</span>
              </div>
            ))}
          </div>
        </div>
      ))}
    </section>
  );
}

export default Skill;
