import React, { useState, useEffect } from "react";
import "../css/Projects.css";

import project1_1 from "../assets/images/project1_portfolio1.png";
import project1_2 from "../assets/images/project1_portfolio2.png";
import project1_3 from "../assets/images/project1_portfolio3.png";
import project2_1 from "../assets/images/project2_portfolio1.png";
import project2_2 from "../assets/images/project2_portfolio1.png";
import project3_1 from "../assets/images/project1_portfolio1.png";
import project3_2 from "../assets/images/project2_portfolio1.png";
import project4_1 from "../assets/images/project1_portfolio1.png";
import project4_2 from "../assets/images/project2_portfolio1.png";

const projectsData = [
  { id: 1, title: "아파! 어디가?",
    period: "2025.01 - 2025.03",
    images: [project1_1, project1_2, project1_3],
    description: "증상을 선택하여 병원을 검색 및 예약 하며 의학 정보를 제공하는 플랫폼입니다.",
    details: {
        overview: "상세 설명 1",
        features: ["주요 기능 1", "주요 기능 2"],
        tech: ["Java", "Spring", "MyBatis"],
        contribution: "개발 100%"
    }
  },
  { id: 2, title: "아틀란 은행",
    period: "2025.01 - 2025.03",
    images: [project2_1, project2_2],
    details: {
        overview: "상세 설명 1",
        features: ["주요 기능 1", "주요 기능 2"],
        tech: ["Java", "Spring", "MyBatis"],
        contribution: "개발 100%"
    }
  },
  { id: 3, title: "프로젝트 3",
    period: "2025.01 - 2025.03",
    images: [project3_1, project3_2],
    description: "간단한 설명 3",
    details: {
        overview: "상세 설명 1",
        features: ["주요 기능 1", "주요 기능 2"],
        tech: ["Java", "Spring", "MyBatis"],
        contribution: "개발 100%"
    }
  },
  { id: 4, title: "프로젝트 4",
    period: "2025.04 - 2025.06",
    images: [project4_1, project4_2],
    description: "간단한 설명 4",
    details: {
        overview: "상세 설명 1",
        features: ["주요 기능 1", "주요 기능 2"],
        tech: ["Java", "Spring", "MyBatis"],
        contribution: "개발 100%"
    }
  },
];

function Projects() {
    const [selected, setSelected] = useState(null);
    const [currentImage, setCurrentImage] = useState(0);

    const openModal = (project) => {
        setSelected(project);
        setCurrentImage(0);
    };
    const closeModal = () => setSelected(null);

    const prevImage = () => {
        if (!selected) return;
        setCurrentImage(
            (prev) => (prev - 1 + selected.images.length) % selected.images.length
        );
    };

    const nextImage = () => {
        if (!selected) return;
        setCurrentImage((prev) => (prev + 1) % selected.images.length);
    };

    // ESC 키로 모달 닫기
    useEffect(() => {
    const handleEsc = (e) => {
      if (e.key === "Escape") closeModal();
    };
    window.addEventListener("keydown", handleEsc);
    return () => window.removeEventListener("keydown", handleEsc);
    }, []);

    return (
      <section id="projects">
        <h2>Projects</h2>
        <div className="project-cards">
          {projectsData.map((p) => (
            <div key={p.id} className="project-card" onClick={() => openModal(p)}>
              <img src={p.images[0]} alt={p.title} className="project-img" />
              <h3>{p.title}</h3>
              <p>{p.description}</p>
            </div>
          ))}
        </div>

        {selected && (
          <div className="modal-overlay" onClick={closeModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <span className="close-btn" onClick={closeModal}>
                &times;
              </span>
              <h3>{selected.title}</h3>
              <p><strong>진행 기간:</strong> {selected.period}</p>

              {/* 이미지 슬라이드 */}
              <div className="modal-images">
                <button onClick={prevImage} className="slide-btn">◀</button>
                <img src={selected.images[currentImage]} alt={selected.title} />
                <button onClick={nextImage} className="slide-btn">▶</button>
              </div>

              <p><strong>간단 설명:</strong> {selected.description}</p>
              <p><strong>주요 기능 및 특징:</strong></p>
              <ul>
                {selected.details.features.map((f, i) => (
                  <li key={i}>{f}</li>
                ))}
              </ul>
              <p><strong>사용 기술 및 언어:</strong> {selected.details.tech.join(", ")}</p>
              <p><strong>작업 기여도:</strong> {selected.details.contribution}</p>
            </div>
          </div>
        )}
      </section>
    );
}

export default Projects;
